  def __del__(self):
    if self.casted_from is None:
      self._ptr.call_with_return(94, ())
      self._ptr = None
  def __setitem__(self,  index, value):
    self._ptr.call_with_return(96, (index, value))

  def __getitem__(self,  index):

    if index < 0:
      raise KeyError(f"Index '{index}' invalid")
    pyobject = self._ptr.call_with_return(95, (index,))
    return pyobject
  def __iter__(self):
    self._index = 0
    return self
  def __next__(self):
    if self._index >= self.size():
      raise StopIteration()
    val = self[self._index]
    self._index += 1
    return val

  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = PlaceholderCubemapArray.construct_without_init()
    cls._ptr = CPPPlaceholderCubemapArrayWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
