from py4godot.wrappers.wrappers import CPPPlaceholderTexture2DWrapper


class PlaceholderTexture2D(py4godot_texture2d.Texture2D):
  r'''
		This class is used when loading a project that uses a `Texture2D` subclass in 2 conditions:
		- When running the project exported in dedicated server mode, only the texture's dimensions are kept (as they may be relied upon for gameplay purposes or positioning of other elements). This allows reducing the exported PCK's size significantly.
		- When this subclass is missing due to using a different engine version or build (e.g. modules disabled).
		**Note:** This is not intended to be used as an actual texture for rendering. It is not guaranteed to work like one in shaders or materials (for example when calculating UV).
	'''

  @staticmethod
  def constructor():
    class_ = PlaceholderTexture2D.construct_without_init()
    class_._ptr = constructor(623,0, ())
    return class_
  @staticmethod
  def new():
    class_ = PlaceholderTexture2D.construct_without_init()
    class_._ptr = constructor(623,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPPlaceholderTexture2DWrapper()
    self.casted_from = None
    if c_utils.shouldCreateObject:
      self._ptr = constructor(623,0, ())


  def generate_wrapper(self):
    return CPPPlaceholderTexture2DWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = PlaceholderTexture2D.__new__(PlaceholderTexture2D)
    cls.shouldBeDeleted = True
    cls.casted_from = None
    cls.constructed_from_python = False
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'PlaceholderTexture2D'

  def __del__(self):
    if self.casted_from is None:
      self._ptr.call_with_return(94, ())
      self._ptr = None
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = PlaceholderTexture2D.construct_without_init()
    cls._ptr = CPPPlaceholderTexture2DWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = PlaceholderTexture2D.construct_without_init()
    cls._ptr = CPPPlaceholderTexture2DWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls

  @property
  def size(self) -> typing.Union['Vector2']:
    r''''''
    _ret = self. get_size()
    return _ret
  @size.setter
  def size(self,  value:'Vector2'):
    self.set_size(value)
  @functools.native_method
  def set_size(self, size:'Vector2'   ) -> None:
    r''''''

    assert(not size is None)
    assert isinstance(size, Vector2), 'size must be Vector2'


    self.py__size = size






    self._ptr.call_with_return(93,tuple([size._ptr]))


register_cast_function('PlaceholderTexture2D', PlaceholderTexture2D.cast)
register_class('PlaceholderTexture2D', PlaceholderTexture2D)
