import py4godot.classes.Popup as py4godot_popup 
from py4godot.wrappers.wrappers import CPPPopupPanelWrapper


class PopupPanel(py4godot_popup.Popup):
  r'''
		A popup with a configurable panel background. Any child controls added to this node will be stretched to fit the panel's size (similar to how `PanelContainer` works). If you are making windows, see `Window`.
	'''

  @staticmethod
  def constructor():
    class_ = PopupPanel.construct_without_init()
    class_._ptr = constructor(635,0, ())
    return class_
  @staticmethod
  def new():
    class_ = PopupPanel.construct_without_init()
    class_._ptr = constructor(635,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPPopupPanelWrapper()
    self.casted_from = None
    if c_utils.shouldCreateObject:
      self._ptr = constructor(635,0, ())


  def generate_wrapper(self):
    return CPPPopupPanelWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = PopupPanel.__new__(PopupPanel)
    cls.shouldBeDeleted = True
    cls.casted_from = None
    cls.constructed_from_python = False
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'PopupPanel'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = PopupPanel.construct_without_init()
    cls._ptr = CPPPopupPanelWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = PopupPanel.construct_without_init()
    cls._ptr = CPPPopupPanelWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('PopupPanel', PopupPanel.cast)
register_class('PopupPanel', PopupPanel)
