from py4godot.wrappers.wrappers import CPPEditorResourceTooltipPluginWrapper


class EditorResourceTooltipPlugin(py4godot_refcounted.RefCounted):
  r'''
		Resource tooltip plugins are used by `FileSystemDock` to generate customized tooltips for specific resources. E.g. tooltip for a `Texture2D` displays a bigger preview and the texture's dimensions.
		A plugin must be first registered with `FileSystemDock.add_resource_tooltip_plugin`. When the user hovers a resource in filesystem dock which is handled by the plugin, `_make_tooltip_for_path` is called to create the tooltip. It works similarly to `Control._make_custom_tooltip`.
	'''

  @staticmethod
  def constructor():
    class_ = EditorResourceTooltipPlugin.construct_without_init()
    class_._ptr = constructor(301,0, ())
    return class_
  @staticmethod
  def new():
    class_ = EditorResourceTooltipPlugin.construct_without_init()
    class_._ptr = constructor(301,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPEditorResourceTooltipPluginWrapper()
    self.casted_from = None
    if c_utils.shouldCreateObject:
      self._ptr = constructor(301,0, ())


  def generate_wrapper(self):
    return CPPEditorResourceTooltipPluginWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = EditorResourceTooltipPlugin.__new__(EditorResourceTooltipPlugin)
    cls.shouldBeDeleted = True
    cls.casted_from = None
    cls.constructed_from_python = False
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'EditorResourceTooltipPlugin'

  def __del__(self):
    if self.casted_from is None:
      self._ptr.call_with_return(56, ())
      self._ptr = None
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = EditorResourceTooltipPlugin.construct_without_init()
    cls._ptr = CPPEditorResourceTooltipPluginWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    cls.casted_from = other
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = EditorResourceTooltipPlugin.construct_without_init()
    cls._ptr = CPPEditorResourceTooltipPluginWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls

  @functools.native_method
  def _handles(self, type:'str'   ) -> typing.Union[bool]:
    r'''
				Return `true` if the plugin is going to handle the given `Resource` `type`.
			'''

    assert(not type is None)
    assert isinstance(type, (str, String)), 'type must be str or String'





    assert(isinstance(type, (str, String)))
    py_string_type = type if isinstance(type, StringName) else c_utils.py_string_to_string(type)

    _ret = 0
    _ret = self._ptr.call_with_return(53,tuple([py_string_type._ptr]))
    return _ret


  @functools.native_method
  def _make_tooltip_for_path(self, path:'str'   , metadata:'Dictionary'   , base:'py4godot_control.Control'   ) -> typing.Any:
    r'''
				Create and return a tooltip that will be displayed when the user hovers a resource under the given `path` in filesystem dock.
				The `metadata` dictionary is provided by preview generator (see `EditorResourcePreviewGenerator._generate`).
				`base` is the base default tooltip, which is a `VBoxContainer` with a file name, type and size labels. If another plugin handled the same file type, `base` will be output from the previous plugin. For best result, make sure the base tooltip is part of the returned `Control`.
				**Note:** It's unadvised to use `ResourceLoader.load`, especially with heavy resources like models or textures, because it will make the editor unresponsive when creating the tooltip. You can use `request_thumbnail` if you want to display a preview in your tooltip.
				**Note:** If you decide to discard the `base`, make sure to call `Node.queue_free`, because it's not freed automatically.
				```gdscript
				func _make_tooltip_for_path(path, metadata, base):
					var t_rect = TextureRect.new()
					request_thumbnail(path, t_rect)
					base.add_child(t_rect) # The TextureRect will appear at the bottom of the tooltip.
					return base
				```
			'''

    assert(not path is None)
    assert isinstance(path, (str, String)), 'path must be str or String'
    assert(not metadata is None)
    assert isinstance(metadata, Dictionary), 'metadata must be Dictionary'
    assert(not base is None)
    assert isinstance(base, get_class('Control')), 'base must be Control'





    assert(isinstance(path, (str, String)))
    py_string_path = path if isinstance(path, StringName) else c_utils.py_string_to_string(path)



    _ret = classes.Object.Object.construct_without_init() #Smart casted to: Control
    _ret._ptr = self._ptr.call_with_return(54,tuple([py_string_path._ptr, metadata._ptr, base._ptr]))
    return smart_cast(_ret) if not _ret._ptr.is_null() else None


  @functools.native_method
  def request_thumbnail(self, path:'str'   , control:'py4godot_texturerect.TextureRect'   ) -> None:
    r'''
				Requests a thumbnail for the given `TextureRect`. The thumbnail is created asynchronously by `EditorResourcePreview` and automatically set when available.
			'''

    assert(not path is None)
    assert isinstance(path, (str, String)), 'path must be str or String'
    assert(not control is None)
    assert isinstance(control, get_class('TextureRect')), 'control must be TextureRect'





    assert(isinstance(path, (str, String)))
    py_string_path = path if isinstance(path, StringName) else c_utils.py_string_to_string(path)


    self._ptr.call_with_return(55,tuple([py_string_path._ptr, control._ptr]))


register_cast_function('EditorResourceTooltipPlugin', EditorResourceTooltipPlugin.cast)
register_class('EditorResourceTooltipPlugin', EditorResourceTooltipPlugin)
