  @functools.native_method
  def set_columns(self, columns:'int'   ) -> None:
    r''''''

    assert isinstance(columns, (int, float)), 'columns must be int or float'


    self.py__columns = columns






    self._ptr.call_with_return(437,tuple([columns]))

