import py4godot.classes as classes
import py4godot.signals as signals

import typing
if typing.TYPE_CHECKING:
  pass
import py4godot.classes.Slider as py4godot_slider 
from py4godot.wrappers.wrappers import CPPHSliderWrapper


class HSlider(py4godot_slider.Slider):
  r'''
		A horizontal slider, used to adjust a value by moving a grabber along a horizontal axis. It is a `Range`-based control and goes from left (min) to right (max).
	'''

  @staticmethod
  def constructor():
    class_ = HSlider.construct_without_init()
    class_._ptr = constructor(395,0, ())
    return class_
  @staticmethod
  def new():
    class_ = HSlider.construct_without_init()
    class_._ptr = constructor(395,0, ())
    return class_
  def __init__(self):
    self._ptr =  CPPHSliderWrapper()
    self.casted_from = None
    if c_utils.shouldCreateObject:
      self._ptr = constructor(395,0, ())


  def generate_wrapper(self):
    return CPPHSliderWrapper()
  def init_signals(self):
    super().init_signals()

  @staticmethod
  def construct_without_init():
    cls = HSlider.__new__(HSlider)
    cls.shouldBeDeleted = True
    cls.casted_from = None
    cls.constructed_from_python = False
    cls.init_signals()
    return cls
  @staticmethod
  def get_type():
    return 'HSlider'

  def __del__(self):
    pass
  @staticmethod
  def cast(other:'Object'):
    assert other != None # Object to be casted must not be None
    cls = HSlider.construct_without_init()
    cls._ptr = CPPHSliderWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls
  @staticmethod
  def cast_without_reference(other: 'Object'):
    assert other != None # Object to be casted must not be None
    cls = HSlider.construct_without_init()
    cls._ptr = CPPHSliderWrapper()
    cls._ptr_before = other._ptr # Save pointer
    cls._ptr.copy_gdowner(cls._ptr_before)
    return cls


register_cast_function('HSlider', HSlider.cast)
register_class('HSlider', HSlider)
