class OneShotRequest:
  ONE_SHOT_REQUEST_NONE = 0
  ONE_SHOT_REQUEST_FIRE = 1
  ONE_SHOT_REQUEST_ABORT = 2
  ONE_SHOT_REQUEST_FADE_OUT = 3
